<script type="cbeebee1d09f036bb3dd0c26-text/javascript">function creerCookie(nom,valeur){var date = new Date();date.setTime(date.getTime()+(365*24*60*60*1000));var exp = '; expires='+date.toGMTString();document.cookie = nom+'='+valeur+exp+'; path=/';}
function lireCookie(nom){var nomEtEgal = nom + '=';var cTableau = document.cookie.split(';');for(var i=0;i<cTableau.length;i++){var c = cTableau[i];while (c.charAt(0)==' ') c = c.substring(1,c.length);if (c.indexOf(nomEtEgal) == 0) return c.substring(nomEtEgal.length,c.length);}return null;}
function masquerDiv(nom){document.getElementById(nom).style.display="none";creerCookie('CookieConsent','CookieConsentOK');}
function afficherDiv(nom){document.getElementById(nom).style.display="visible";}			
